import React from 'react';
import { useEffect, useState } from 'react';
import { Card, Image, Modal, Button, Container, Row, Col } from 'react-bootstrap';

function Question({ question }) {

    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>

            <Card bg="secondary" className="mb-2 w-100">
                <Card.Body className="pl-1 pr-1 p-2 d-flex">
                    {/* <FaFlag height={35} className="my-auto mr-2" color={"red"} /> */}
                    <h5 className="my-auto mr-auto BoxContentText">{question.title}</h5>
                    {/* Open modal */}
                    <a onClick={handleShow} className="my-auto BoxContentLink mr-2" >View report details</a>
                </Card.Body>
            </Card>
            {/* Modal */}
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
          </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
export default Question
