import React from 'react';
import { useEffect, useState } from 'react';
import { Card, Image, Modal, Button, Container, Row, Col } from 'react-bootstrap';
import axios from "axios"
import "../styling/Question.css"


function Question({ comments, refreshComments }) {

    const [show, setShow] = useState(false);
    const [date, setDate] = useState();
    const [comment, setComment] = useState();

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    return (
        <>

                <Row>
                    <Col md="3">
                        <Button onClick={handleShow}>Comments</Button>
                        </Col>
                </Row>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Delete Comments</Modal.Title>
                </Modal.Header>
                {comments.map((comment, idx) => (
                    <Modal.Body>{comment.text}</Modal.Body>
                ))}
                <Modal.Footer>
                    <Button variant="primary" onClick={handleClose}>
                        Close
          </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
export default Question
