import React from 'react';
import { useEffect, useState } from 'react';
import { Card, Image, Modal, Button, Container, Row, Col } from 'react-bootstrap';

function Question({ question }) {

    const [show, setShow] = useState(false);
    const [date, setDate] = useState();

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    function HumanDateTime(dates) {
        var date = new Date(dates + "Z");
        date = date.toUTCString().split(", ");
        date = date[1].slice(0, 17);
        setDate(date);
      }
    
      useEffect(() => {
        if (question.deletionTime != undefined) {
          HumanDateTime(question.deletionTime);
        }
      }, [question.deleteTime]);

    return (
        <>
            <div className="rounded container">
                <Row>
                    <Col md="1">{question.id}</Col>
                    <Col md="2">{question.title}</Col>
                    <Col md="1">{question.tag}</Col>
                    <Col md="1">{date}</Col>
                    <Col md="2">{question.commentsEnabled}</Col>
                </Row>
            </div>
            {/* <Card bg="secondary" className="mb-2 w-80">
                <Card.Body>
                    <Row> 
                        <Col md="1">
                        <h5>{question.title}</h5>
                        </Col>
                    </Row>
                    <a onClick={handleShow} className="my-auto BoxContentLink mr-2" >View report details</a>
                </Card.Body>
            </Card> */}
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                    <Modal.Title>Modal heading</Modal.Title>
                </Modal.Header>
                <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
          </Button>
                    <Button variant="primary" onClick={handleClose}>
                        Save Changes
          </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
export default Question
