                </Modal.Header>
                <Modal.Body>Are you sure you want to delete?</Modal.Body>
                <Modal.Footer>
                    <Button variant="secondary" onClick={handleClose}>
                        Close
          </Button>
                    <Button variant="primary"  onClick={handleClose}>
                        Delete Question
          </Button>
                </Modal.Footer>
            </Modal>
        </>
    );
}
export default Question
