import http from "../common-api";

const PostComment = (data) => {
    return http.httpdefault().post("/Comment", data);
  };
const GetComments = (id) => {
    return http.httpdefault().get("/Comment/" + id + "/GetAllCommentsID");
  };
  

export default {
  PostComment,
  GetComments
};
